--------------------------------------------------------
--  DDL for View VEH_FIN_HEATS_CONFIRM
--------------------------------------------------------

  CREATE OR REPLACE FORCE VIEW "VEH_FIN_HEATS_CONFIRM" ("ID", "S_HEAT_ID", "HEAT_NO", "C_HEAT_STATUS_ID", "C_PLANT_UNIT_ID", "CE_PRACTICE_ID", "C_STEEL_GRADE_ID", "START_TIME", "END_TIME", "C_PHASE_ID", "NUM_OF_BASKETS", "NUM_OF_HOT_METAL", "STEEL_WEIGHT", "SLAG_WEIGHT", "HOT_HEEL_WEIGHT", "STEEL_TEMPERATURE", "START_TEMPERATURE", "TAP_TEMPERATURE", "POWER_ON_TIME", "T1_TIME", "ENERGY_TOTAL", "SCRAP_WEIGHT", "DRI_WEIGHT", "LIME_WEIGHT", "DOLO_WEIGHT", "C_CONTENT", "O_CONTENT", "OXYGEN_CONS", "MIX_GAS_CONS", "COKE_CONS", "METHANE_CONS", "NITROGEN_CONS", "LADLE_WEIGHT", "TAP_START", "TAP_END", "TAPPING_TIME", "LADLE_TARE_WEIGHT", "LADLE_STEEL_NET_WEIGHT", "TAP_SLAG_WEIGHT", "SLAG_PERCENTAGE", "HOT_TAPPED", "HOT_TAP_REASON", "VERIFIED", "VER_COMMENT", "CREATED", "MODIFIED", "MOP") AS 
  SELECT "ID",    "S_HEAT_ID",    (SELECT HEAT_NO FROM S_HEAT sh WHERE sh.ID="S_HEAT_ID"    ) AS HEAT_NO,    "C_HEAT_STATUS_ID",    "C_PLANT_UNIT_ID",    "CE_PRACTICE_ID",    "C_STEEL_GRADE_ID",    "START_TIME",    "END_TIME",    "C_PHASE_ID",    "NUM_OF_BASKETS",    "NUM_OF_HOT_METAL",    "STEEL_WEIGHT",    "SLAG_WEIGHT",    "HOT_HEEL_WEIGHT",    "STEEL_TEMPERATURE",    "START_TEMPERATURE",    "TAP_TEMPERATURE",    "POWER_ON_TIME",    "T1_TIME",    "ENERGY_TOTAL",    "SCRAP_WEIGHT",    "DRI_WEIGHT",    "LIME_WEIGHT",    "DOLO_WEIGHT",    "C_CONTENT",    "O_CONTENT",    "OXYGEN_CONS",    "MIX_GAS_CONS",    "COKE_CONS",    "METHANE_CONS",    "NITROGEN_CONS",    "LADLE_WEIGHT",    "TAP_START",    "TAP_END",    "TAPPING_TIME",    "LADLE_TARE_WEIGHT",    "LADLE_STEEL_NET_WEIGHT",    "TAP_SLAG_WEIGHT",    "SLAG_PERCENTAGE",    "HOT_TAPPED",    "HOT_TAP_REASON",    "VERIFIED",    "VER_COMMENT",    "CREATED",    "MODIFIED",    "MOP"  FROM PE_HEAT  WHERE C_HEAT_STATUS_ID=GET_HEAT_STATUS_ID(21);
